import speech_recognition as sr

# Initialize recognizer
recognizer = sr.Recognizer()

# Path to the WAV file
wav_path = &quot;/mnt/data/Tuesday_2h15m_AM.wav&quot;

# Load the audio file
with sr.AudioFile(wav_path) as source:
    audio_data = recognizer.record(source)

# Try transcribing
try:
    transcription = recognizer.recognize_google(audio_data)
except sr.UnknownValueError:
    transcription = &quot;[Audio unclear — could not transcribe]&quot;
except sr.RequestError as e:
    transcription = f&quot;[Error with speech recognition service: {e}]&quot;

transcription[:1000]  # Preview the first part of the transcription

