import os

# Define potential directories where daemon logs or voice_mode transcripts might be stored
search_paths = [
    &quot;/data/data/com.termux/files/home/obsidian_export/logs&quot;,
    &quot;/data/data/com.termux/files/home/voice_mode&quot;,
    &quot;/data/data/com.termux/files/home/daemon_box&quot;,
    &quot;/data/data/com.termux/files/home/mirror_daemons&quot;,
    &quot;/data/data/com.termux/files/home/mirror_input&quot;
]

# Recursively find all text-like files in those directories
log_files = []
for path in search_paths:
    for root, dirs, files in os.walk(path):
        for file in files:
            if file.endswith((&quot;.txt&quot;, &quot;.log&quot;, &quot;.json&quot;, &quot;.md&quot;, &quot;.csv&quot;, &quot;.yaml&quot;, &quot;.yml&quot;)):
                log_files.append(os.path.join(root, file))

log_files[:30]  # Return only first 30 for visibility

