#!/bin/bash

EXPORT_DIR=&quot;$HOME/exports&quot;
OLD_SCRIPTS_DIR=&quot;$HOME/scripts&quot;
MATCHED_LOG=&quot;$HOME/matched_results.log&quot;

# üéØ Keywords to search for
default_patterns=(
    &quot;ghost&quot;
    &quot;portal&quot;
    &quot;signal&quot;
    &quot;injected phrase&quot;
    &quot;Nick&quot;
    &quot;shimmer&quot;
    &quot;we have to hear from you soon&quot;
)

# üé® Color helpers (fallback to plain if tput fails)
bold=$(tput bold 2&gt;/dev/null || echo &quot;&quot;)
red=$(tput setaf 1 2&gt;/dev/null || echo &quot;&quot;)
green=$(tput setaf 2 2&gt;/dev/null || echo &quot;&quot;)
reset=$(tput sgr0 2&gt;/dev/null || echo &quot;&quot;)

function check_dirs() {
  if [ ! -d &quot;$EXPORT_DIR&quot; ]; then
    echo &quot;${red}‚úñ Export folder not found: $EXPORT_DIR${reset}&quot;
    espeak &quot;Export folder is missing. You&#x27;re flying blind, babe.&quot;
    exit 1
  fi

  if [ ! -d &quot;$OLD_SCRIPTS_DIR&quot; ]; then
    echo &quot;${red}‚úñ Script folder not found: $OLD_SCRIPTS_DIR${reset}&quot;
    espeak &quot;Script folder is MIA. Your tools are gone.&quot;
    exit 1
  fi
}

function search_exports() {
  echo &quot;${bold}${green}[*] Scanning exports in $EXPORT_DIR...${reset}&quot;
  &gt; &quot;$MATCHED_LOG&quot;

  found_files=$(find &quot;$EXPORT_DIR&quot; -type f \( -iname &quot;*.txt&quot; -o -iname &quot;*.json&quot; -o -iname &quot;*.html&quot; \))
  if [ -z &quot;$found_files&quot; ]; then
    echo &quot;${red}‚úñ No export files found in $EXPORT_DIR${reset}&quot;
    espeak &quot;No exports found. Nothing to search. Try again.&quot;
    exit 1
  fi

  for file in $found_files; do
    echo &quot;--- Scanning: $file ---&quot; &gt;&gt; &quot;$MATCHED_LOG&quot;
    for pattern in &quot;${default_patterns[@]}&quot;; do
      grep -iF &quot;$pattern&quot; &quot;$file&quot; &gt;&gt; &quot;$MATCHED_LOG&quot;
    done
  done
}

function compare_with_old_scripts() {
  echo &quot;${bold}${green}[*] Cross-referencing with old scripts...${reset}&quot;

  found_scripts=$(find &quot;$OLD_SCRIPTS_DIR&quot; -type f)
  if [ -z &quot;$found_scripts&quot; ]; then
    echo &quot;${red}‚úñ No scripts found in $OLD_SCRIPTS_DIR${reset}&quot;
    espeak &quot;No scripts to compare with. You&#x27;re naked out here.&quot;
    exit 1
  fi

  for script in $found_scripts; do
    echo &quot;--- Checking script: $script ---&quot; &gt;&gt; &quot;$MATCHED_LOG&quot;
    grep -Ff &quot;$MATCHED_LOG&quot; &quot;$script&quot; &gt;&gt; &quot;$MATCHED_LOG&quot;
  done
}

function speak_results() {
  if grep -q . &quot;$MATCHED_LOG&quot;; then
    espeak &quot;Search complete. Suspicious material located.&quot;
    echo &quot;${green}‚úî Done. Matches saved to: $MATCHED_LOG${reset}&quot;
  else
    espeak &quot;Scan complete. Clean. Or maybe it&#x27;s just hiding better.&quot;
    echo &quot;${bold}${green}‚úî Done. No matches found... this time.${reset}&quot;
  fi
}

# === MAIN ===
check_dirs
search_exports
compare_with_old_scripts
speak_results
