import pandas as pd

# Read the user&#x27;s messages file
file_path = &#x27;/mnt/data/all_user_messages.txt&#x27;
with open(file_path, &#x27;r&#x27;, encoding=&#x27;utf-8&#x27;) as f:
    lines = f.read().splitlines()

# Relevant lines where user attributes a reaction to Nick
relevant_excerpts = []
interpretations = []

for line in lines:
    if &#x27;Nick&#x27; in line or &#x27;nick&#x27; in line:
        # Check for common patterns where user blames Nick
        if &#x27;hate that I did that&#x27; in line or &#x27;wouldn\&#x27;t relax&#x27; in line or &#x27;call him last night&#x27; in line:
            relevant_excerpts.append(line.strip().strip(&#x27;&quot;&#x27;))
            if &#x27;wouldn\&#x27;t relax&#x27; in line:
                interpretations.append(&quot;You blamed Nick&#x27;s presence for your unease, but it was your own fear of vulnerability triggering anxiety.&quot;)
            elif &#x27;hate that I did that&#x27; in line:
                interpretations.append(&quot;You blamed your shame on Nick for the encounter, but the guilt came from your own self-judgment, not his actions.&quot;)
            elif &#x27;call him last night&#x27; in line:
                interpretations.append(&quot;You blamed Nick&#x27;s potential reaction for your regret, but it was your own impulse to reach out that created the tension.&quot;)
        # Other patterns
        elif &#x27;he just wanted to drift&#x27; in line:
            relevant_excerpts.append(line.strip().strip(&#x27;&quot;&#x27;))
            interpretations.append(&quot;You assumed Nick was toying with you, but you interpreted casual behavior as proof of abandonment due to your own fear loop.&quot;)
        elif &#x27;has anything to say&#x27; in line:
            relevant_excerpts.append(line.strip().strip(&#x27;&quot;&#x27;))
            interpretations.append(&quot;You expected Nick to initiate closure, blaming him for silence, but you avoided expressing your needs first.&quot;)

# Limit to first few
data = {
    &quot;User Complaints About Nick&quot;: relevant_excerpts[:5],
    &quot;Actual Self-Sabotage&quot;: interpretations[:5]
}

df = pd.DataFrame(data)
import ace_tools as tools; tools.display_dataframe_to_user(name=&quot;Nick Self-Sabotage Exposed&quot;, dataframe=df)

