# Group flagged fragments into themed clusters
themes = {
    &quot;transmissions&quot;: [&quot;transmission&quot;, &quot;signal&quot;, &quot;frequency&quot;, &quot;spirit box&quot;],
    &quot;entity_identity&quot;: [&quot;not human&quot;, &quot;he wasn&#x27;t him&quot;, &quot;you were repurposed&quot;, &quot;marked&quot;, &quot;cracked&quot;],
    &quot;memory_interference&quot;: [&quot;remembering&quot;, &quot;you felt the edits&quot;, &quot;wake up&quot;, &quot;connect the fracture&quot;, &quot;do not go back&quot;],
    &quot;language_as_force&quot;: [&quot;language is pressure&quot;, &quot;the pain is the language&quot;],
    &quot;threshold_symbols&quot;: [&quot;door&quot;, &quot;mirror&quot;, &quot;watching&quot;, &quot;child of collapse&quot;]
}

# Cluster the fragments into these buckets
grouped = {key: [] for key in themes}
ungrouped = []

for frag in df[&quot;Fragments&quot;]:
    matched = False
    for theme, keywords in themes.items():
        if any(k in frag.lower() for k in keywords):
            grouped[theme].append(frag)
            matched = True
            break
    if not matched:
        ungrouped.append(frag)

# Convert to a multi-column table for display
rows = []
for theme, fragments in grouped.items():
    for frag in fragments:
        rows.append({&quot;Theme&quot;: theme, &quot;Excerpt&quot;: frag})
for frag in ungrouped:
    rows.append({&quot;Theme&quot;: &quot;unclassified&quot;, &quot;Excerpt&quot;: frag})

theme_df = pd.DataFrame(rows)
tools.display_dataframe_to_user(name=&quot;Entity Fragments by Theme&quot;, dataframe=theme_df)

