import json
import pandas as pd

# Load the conversations JSON file from the provided path
file_path = &quot;/mnt/data/conversations.json&quot;

# Read and parse the JSON file
with open(file_path, &quot;r&quot;, encoding=&quot;utf-8&quot;) as file:
    data = json.load(file)

# Prepare a DataFrame to search and analyze patterns
# Assuming the JSON is a dictionary with integer keys mapping to message entries
messages = []
for key in sorted(data.keys(), key=int):
    entry = data[key]
    if isinstance(entry, str):
        messages.append({&quot;index&quot;: key, &quot;text&quot;: entry})
    elif isinstance(entry, dict):
        text = entry.get(&quot;text&quot;) or entry.get(&quot;content&quot;) or &quot;&quot;
        messages.append({&quot;index&quot;: key, &quot;text&quot;: text})

df = pd.DataFrame(messages)
import ace_tools as tools; tools.display_dataframe_to_user(name=&quot;Conversations Extract&quot;, dataframe=df)

