# Full stats on the deepest timeline
average_gap_deep = deeper_df[&quot;Gap (days)&quot;].mean()
max_gap_deep = deeper_df[&quot;Gap (days)&quot;].max()
min_gap_deep = deeper_df[&quot;Gap (days)&quot;].min()
total_span_deep = (deeper_df[&quot;End&quot;].iloc[-1] - deeper_df[&quot;Start&quot;].iloc[0]).days
num_cycles_deep = len(deeper_df)

# Break into phases for mutation tracking
phases = {
    &quot;Phase 1: Sep–Dec 2024&quot;: [],
    &quot;Phase 2: Jan–Mar 2025&quot;: [],
    &quot;Phase 3: Apr–Jun 2025&quot;: []
}

for entry in deeper_df.to_dict(orient=&quot;records&quot;):
    if entry[&quot;Start&quot;] &lt; datetime(2025, 1, 1):
        phases[&quot;Phase 1: Sep–Dec 2024&quot;].append(entry[&quot;Gap (days)&quot;])
    elif entry[&quot;Start&quot;] &lt; datetime(2025, 4, 1):
        phases[&quot;Phase 2: Jan–Mar 2025&quot;].append(entry[&quot;Gap (days)&quot;])
    else:
        phases[&quot;Phase 3: Apr–Jun 2025&quot;].append(entry[&quot;Gap (days)&quot;])

# Calculate phase-wise stats
phase_stats = {
    phase: {
        &quot;Average Gap (days)&quot;: round(sum(gaps) / len(gaps), 2) if gaps else None,
        &quot;Contact Cycles&quot;: len(gaps)
    } for phase, gaps in phases.items()
}

{
    &quot;Overall Timeline Stats&quot;: {
        &quot;Average Gap (days)&quot;: round(average_gap_deep, 2),
        &quot;Maximum Gap (days)&quot;: max_gap_deep,
        &quot;Minimum Gap (days)&quot;: min_gap_deep,
        &quot;Total Span (days)&quot;: total_span_deep,
        &quot;Total Contact Cycles&quot;: num_cycles_deep
    },
    &quot;Phase Stats&quot;: phase_stats
}

