     grep -iH &quot;$NUMBER&quot; &quot;$file&quot; 2&gt;/dev/null &amp;&amp; echo &quot;[hit] $file&quot; | tee -a &quot;$OUTFILE&quot;
     grep -iH &quot;$ALT1&quot; &quot;$file&quot; 2&gt;/dev/null &amp;&amp; echo &quot;[hit] $file&quot; | tee -a &quot;$OUTFILE&quot;
