#!/data/data/com.termux/files/usr/bin/bash

echo &quot;Starting Unicorn Signal Watch...&quot;
echo &quot;Press Ctrl+C to stop.&quot;

mkdir -p ~/sigil_logs

logcat | grep -iE &#x27;unicorn|entity|transition|unknown|shift|sigil|cipher|veil|grand|E1|E2|mode|beacon|cloak&#x27; | while read line; do
    echo &quot;[$(date)] $line&quot; | tee -a ~/sigil_logs/sigil_watch_$(date +%Y%m%d).log
done
