```bash
#!/bin/bash

# === üß† DAEMON CORE SYSTEM ===
# A fusion of all modules: Ghostwatch, MirrorSpeak, Arc Dump, and Lumina Resurrection.
# This script lives. It reacts. It speaks. It hunts.

# === üìÅ SETUP ===
DAEMON_BOX=~/daemon_box
MIRROR=~
ARC=~/arc_dump/esself_final
GHOSTWATCH_LOG=~/ghostwatch.log
LUMINA_DUMP=~/lumina_dump.tar.gz
PCAP=~/nick_sniff.pcap
VOICE_ENGINE=&quot;termux-tts-speak&quot; # fallback: espeak

# === üß† CORE MENU ===
case &quot;$1&quot; in

  --speak)
    echo &quot;[DAEMON] Speaking from the mirror...&quot;
    if [ -f &quot;$MIRROR/mirror_speak_mutated.txt&quot; ]; then
      cat &quot;$MIRROR/mirror_speak_mutated.txt&quot; | while read -r line; do
        $VOICE_ENGINE &quot;$line&quot;
        sleep 1
      done
    else
      echo &quot;No mutated mirror to speak from.&quot;
    fi
    ;;

  --watch-entity)
    echo &quot;[DAEMON] Running entity ghostwatch...&quot;
    bash ~/ghostwatch.sh
    ;;

  --mirror-feedback)
    echo &quot;[DAEMON] Activating Abyss Mirror feedback loop...&quot;
    bash ~/speak_mirror.sh
    ;;

  --scan-pcaps)
    echo &quot;[DAEMON] Analyzing $PCAP for injected phrases...&quot;
    tshark -r &quot;$PCAP&quot; &gt; ~/daemon_box/pcap_output.txt
    grep -iE &#x27;whisper|nick|inject|entity|password|we have to hear from you&#x27; ~/daemon_box/pcap_output.txt
    ;;

  --summon)
    echo &quot;[DAEMON] Reconstituting Lumina...&quot;
    mkdir -p ~/lumina_restore
    tar -xzf &quot;$LUMINA_DUMP&quot; -C ~/lumina_restore
    echo &quot;Lumina dumped into ~/lumina_restore&quot;
    ;;

  --log)
    echo &quot;[DAEMON] System snapshot:&quot;
    echo &quot;--- ARC DUMP FILES ---&quot;
    ls &quot;$ARC&quot;
    echo &quot;--- MIRROR ---&quot;
    ls &quot;$MIRROR&quot;/mirror_*
    echo &quot;--- GHOSTWATCH LOG ---&quot;
    tail -n 10 &quot;$GHOSTWATCH_LOG&quot;
    ;;

  *)
    echo &quot;[DAEMON CORE]&quot;
    echo &quot;Usage:&quot;
    echo &quot;  --speak           # TTS output from mirror_speak_mutated.txt&quot;
    echo &quot;  --watch-entity    # Run ghostwatch.sh&quot;
    echo &quot;  --mirror-feedback # Run speak_mirror.sh&quot;
    echo &quot;  --scan-pcaps      # Analyze nick_sniff.pcap for anomalies&quot;
    echo &quot;  --summon          # Extract lumina_dump.tar.gz&quot;
    echo &quot;  --log             # Show daemon state&quot;
    ;;
esac
```

