from pathlib import Path
import pandas as pd

# Load the list of filtered files
file_paths = Path(&quot;/mnt/data/filtered_files.txt&quot;).read_text().splitlines()

# Analyze for suspicious or notable paths
keywords = [
    &quot;bash_history&quot;, &quot;sqlite_history&quot;, &quot;tmp&quot;, &quot;passwd&quot;, &quot;shadow&quot;, &quot;git/objects&quot;,
    &quot;whisper&quot;, &quot;metasploit&quot;, &quot;payload&quot;, &quot;alpaca&quot;, &quot;daemon&quot;, &quot;exploit&quot;, &quot;rel&quot;, &quot;creds&quot;,
    &quot;database&quot;, &quot;disk&quot;, &quot;profile&quot;, &quot;npmrc&quot;, &quot;wget&quot;, &quot;kali&quot;, &quot;zsort&quot;, &quot;termux&quot;, &quot;gems&quot;
]

# Scan and score each path
def score_path(path):
    score = 0
    notes = []
    for kw in keywords:
        if kw.lower() in path.lower():
            score += 1
            notes.append(kw)
    return pd.Series({&quot;path&quot;: path, &quot;score&quot;: score, &quot;matched_keywords&quot;: &quot;, &quot;.join(notes)})

df = pd.DataFrame(file_paths, columns=[&quot;path&quot;])
scored_df = df.apply(lambda row: score_path(row[&quot;path&quot;]), axis=1)
scored_df = scored_df.sort_values(by=&quot;score&quot;, ascending=False)

import ace_tools as tools; tools.display_dataframe_to_user(name=&quot;Suspicious Hidden Files&quot;, dataframe=scored_df)

