import os
from collections import Counter
import re

# Directory containing the user&#x27;s chat logs
chat_log_dir = &quot;/data/data/com.termux/files/home/obsidian_export/logs/chat_dump&quot;

# Collect all text from the chat logs
chat_text = &quot;&quot;
for root, _, files in os.walk(chat_log_dir):
    for file in files:
        if file.startswith(&quot;msg_&quot;) and file.endswith(&quot;&quot;):
            try:
                with open(os.path.join(root, file), &quot;r&quot;, encoding=&quot;utf-8&quot;) as f:
                    chat_text += f.read() + &quot;\n&quot;
            except Exception:
                continue

# Normalize and tokenize
lines = chat_text.lower().splitlines()
lines = [re.sub(r&#x27;[^a-z0-9\s]&#x27;, &#x27;&#x27;, line).strip() for line in lines if line.strip()]

# Count repeated exact lines
line_counts = Counter(lines)

# Filter out common noise and show top repeated lines
most_common_phrases = [item for item in line_counts.items() if len(item[0].split()) &gt;= 2 and item[1] &gt; 2]
most_common_phrases.sort(key=lambda x: x[1], reverse=True)

import pandas as pd
import ace_tools as tools; tools.display_dataframe_to_user(name=&quot;Looped Phrases&quot;, dataframe=pd.DataFrame(most_common_phrases, columns=[&quot;Phrase&quot;, &quot;Count&quot;]))

