from pathlib import Path
import pandas as pd
import re

# Raw file list from user
raw_text = &#x27;&#x27;&#x27;(User provided massive file list)&#x27;&#x27;&#x27;

# Extract valid script paths from raw input
script_paths = re.findall(r&#x27;/data/data/com\.termux/files/home/[^\s]+?\.sh&#x27;, raw_text)

# Parse paths into categories
def categorize(path):
    if &quot;daemon_box&quot; in path:
        return &quot;Daemon Box&quot;
    elif &quot;whisper.cpp&quot; in path:
        return &quot;Whisper.cpp&quot;
    elif &quot;llama.cpp&quot; in path:
        return &quot;LLaMA.cpp&quot;
    elif &quot;metasploit-framework&quot; in path:
        return &quot;Metasploit&quot;
    elif &quot;termux-root-packages&quot; in path:
        return &quot;Termux Root Packages&quot;
    elif &quot;koboldcpp&quot; in path:
        return &quot;KoboldCPP&quot;
    elif &quot;lumina_search&quot; in path:
        return &quot;Lumina&quot;
    elif &quot;control.sh&quot; in path:
        return &quot;Root Control Script&quot;
    elif &quot;logspy&quot; in path:
        return &quot;LogSpy&quot;
    elif &quot;scripts/&quot; in path:
        return &quot;User Scripts&quot;
    elif &quot;start-&quot; in path or &quot;init&quot; in path:
        return &quot;Startup / Init&quot;
    else:
        return &quot;Other&quot;

# Create DataFrame
df = pd.DataFrame(script_paths, columns=[&quot;Path&quot;])
df[&quot;Category&quot;] = df[&quot;Path&quot;].apply(categorize)
df_sorted = df.sort_values(by=[&quot;Category&quot;, &quot;Path&quot;])

import ace_tools as tools; tools.display_dataframe_to_user(name=&quot;Termux Script Inventory&quot;, dataframe=df_sorted)

