# Script to parse and summarize exported chats and notes into a life event timeline

import os
import re
import json
from datetime import datetime

ROOT_DIR = os.path.expanduser(\&quot;~/\&quot;)  # adjust as needed
TARGET_EXTENSIONS = [\&quot;.txt\&quot;, \&quot;.json\&quot;]

EVENT_KEYWORDS = [
    \&quot;breakup\&quot;, \&quot;dream\&quot;, \&quot;realization\&quot;, \&quot;spirit\&quot;, \&quot;entity\&quot;, \&quot;Nick\&quot;,
    \&quot;revelation\&quot;, \&quot;pattern\&quot;, \&quot;signal\&quot;, \&quot;hurt\&quot;, \&quot;healing\&quot;, \&quot;mirror\&quot;, \&quot;loop\&quot;
]

DATE_PATTERNS = [
    r\&quot;\b\d{4}-\d{2}-\d{2}\b\&quot;,  # 2025-07-04
    r\&quot;\b\d{2}/\d{2}/\d{4}\b\&quot;,  # 07/04/2025
    r\&quot;\b\d{1,2} ?[A-Za-z]{3,9} ?\d{4}\b\&quot;,  # 4 July 2025
]

def scan_files(root):
    for dirpath, _, filenames in os.walk(root):
        for f in filenames:
            if any(f.endswith(ext) for ext in TARGET_EXTENSIONS):
                yield os.path.join(dirpath, f)

def extract_text(file_path):
    try:
        with open(file_path, &#x27;r&#x27;, encoding=&#x27;utf-8&#x27;, errors=&#x27;ignore&#x27;) as f:
            return f.read()
    except:
        return \&quot;\&quot;

def extract_dates(text):
    dates = set()
    for pattern in DATE_PATTERNS:
        matches = re.findall(pattern, text)
        for match in matches:
            try:
                dates.add(str(datetime.strptime(match, \&quot;%Y-%m-%d\&quot;)))
            except:
                pass
    return sorted(dates)

def find_event_lines(text):
    lines = text.splitlines()
    events = []
    for i, line in enumerate(lines):
        for keyword in EVENT_KEYWORDS:
            if keyword.lower() in line.lower():
                context = &#x27;\n&#x27;.join(lines[max(0, i-1):i+2])
                events.append((keyword, context))
    return events

def assemble_timeline():
    timeline = []
    for filepath in scan_files(ROOT_DIR):
        content = extract_text(filepath)
        dates = extract_dates(content)
        events = find_event_lines(content)
        for keyword, context in events:
            timeline.append({
                \&quot;file\&quot;: filepath,
                \&quot;keyword\&quot;: keyword,
                \&quot;context\&quot;: context.strip(),
                \&quot;dates\&quot;: dates
            })
    return sorted(timeline, key=lambda x: x[&#x27;dates&#x27;][0] if x[&#x27;dates&#x27;] else &#x27;&#x27;)

def save_timeline(timeline, output=\&quot;~/ghost_mirror/life_timeline.json\&quot;):
    output_path = os.path.expanduser(output)
    os.makedirs(os.path.dirname(output_path), exist_ok=True)
    with open(output_path, &#x27;w&#x27;, encoding=&#x27;utf-8&#x27;) as f:
        json.dump(timeline, f, indent=2)

if __name__ == &#x27;__main__&#x27;:
    timeline = assemble_timeline()
    save_timeline(timeline)
    print(f\&quot;[✓] Timeline assembled with {len(timeline)} entries. Saved to ghost_mirror/life_timeline.json\&quot;)&quot;}

