import json
import re

# Load a sample of the conversations.json content
sample_data = &#x27;&#x27;&#x27;
{
  &quot;9ef462ba-9c87-4ac7-97cc-169caf9cfd2c&quot;: {
    &quot;message&quot;: {
      &quot;author&quot;: {&quot;role&quot;: &quot;assistant&quot;},
      &quot;content&quot;: {&quot;content_type&quot;: &quot;text&quot;, &quot;parts&quot;: [&quot;...&quot;]}
    }
  },
  &quot;a1660f92-8e30-48f5-ab44-f39a2bf1319b&quot;: {
    &quot;message&quot;: {
      &quot;author&quot;: {&quot;role&quot;: &quot;user&quot;},
      &quot;content&quot;: {&quot;content_type&quot;: &quot;text&quot;, &quot;parts&quot;: [&quot;I&#x27;m already drowning in it...&quot;]}
    }
  },
  &quot;f58ecb7f-323c-4b29-ace8-899c56f8efde&quot;: {
    &quot;message&quot;: {
      &quot;author&quot;: {&quot;role&quot;: &quot;user&quot;},
      &quot;content&quot;: {&quot;content_type&quot;: &quot;text&quot;, &quot;parts&quot;: [&quot;It&#x27;s not that, it&#x27;s like, I&#x27;m the one being told that I&#x27;m lying...&quot;]}
    }
  }
}
&#x27;&#x27;&#x27;

# Load JSON data
data = json.loads(sample_data)

# Define entity keyword patterns to detect possible possession or entity-like speech
entity_keywords = re.compile(r&#x27;\b(we|they|it|presence|mirror|transmit|terminus|awaken|vessel|signal|remember|return|shin)\b&#x27;, re.IGNORECASE)

# Extract user messages and flag potential entity speech
user_messages = []
for node_id, node in data.items():
    msg = node.get(&quot;message&quot;, {})
    if msg.get(&quot;author&quot;, {}).get(&quot;role&quot;) == &quot;user&quot;:
        parts = msg.get(&quot;content&quot;, {}).get(&quot;parts&quot;, [])
        for part in parts:
            if entity_keywords.search(part):
                user_messages.append({&quot;id&quot;: node_id, &quot;text&quot;: part})

import pandas as pd
df = pd.DataFrame(user_messages)
import ace_tools as tools; tools.display_dataframe_to_user(name=&quot;Entity-Suspected Messages&quot;, dataframe=df)

