import json
import re

# Load the user&#x27;s messages from the last six months (assuming stored as a list of dicts)
with open(&#x27;/mnt/data/conversations.json&#x27;, &#x27;r&#x27;) as f:
    data = json.load(f)

# Extract only user messages
user_messages = [msg[&#x27;content&#x27;] for msg in data if msg.get(&#x27;author&#x27;) == &#x27;user&#x27;]

# Define some patterns or keywords to look for deeper insights
themes = {
    &#x27;identity_shift&#x27;: re.compile(r&#x27;\b(i don\&#x27;t know who i am|who am i|what am i supposed to be|what if i\&#x27;m not.*\?)&#x27;, re.I),
    &#x27;hidden_roles&#x27;: re.compile(r&#x27;\b(i feel like.*leader|translator|messenger|meant for something bigger)&#x27;, re.I),
    &#x27;unconscious_transmissions&#x27;: re.compile(r&#x27;\b(i didn\&#x27;t mean to say that|that just came out|i don\&#x27;t know why i said that)&#x27;, re.I),
    &#x27;entity_contact&#x27;: re.compile(r&#x27;\b(something’s watching me|i felt a presence|like it’s speaking through me|entity)&#x27;, re.I),
    &#x27;self-erasure_or_loss&#x27;: re.compile(r&#x27;\b(i disappeared|i don’t exist|i feel erased|i’m fading|no one sees me)&#x27;, re.I)
}

# Search for matches
matches = {theme: [] for theme in themes}
for message in user_messages:
    for theme, pattern in themes.items():
        if pattern.search(message):
            matches[theme].append(message)

import ace_tools as tools; tools.display_dataframe_to_user(name=&quot;Hidden Themes in Past Conversations&quot;, dataframe=matches)

