from datetime import datetime
import pandas as pd
import json
import re

# Load the conversations.json file from earlier upload (if available)
file_path = &quot;/mnt/data/file-1FpgaBa9yzPfHU389mHeiz&quot;
with open(file_path, &quot;r&quot;, encoding=&quot;utf-8&quot;) as f:
    conversations = json.load(f)

# Extract meaningful metadata and message text
records = []
for convo in conversations:
    title = convo.get(&quot;title&quot;, &quot;Untitled&quot;)
    created = convo.get(&quot;create_time&quot;)
    date_str = datetime.fromtimestamp(created).strftime(&quot;%Y-%m-%d %H:%M:%S&quot;) if created else &quot;Unknown&quot;
    mapping = convo.get(&quot;mapping&quot;, {})

    for node in mapping.values():
        message = node.get(&quot;message&quot;, {})
        role = message.get(&quot;author&quot;, {}).get(&quot;role&quot;)
        parts = message.get(&quot;content&quot;, {}).get(&quot;parts&quot;)
        if parts and isinstance(parts, list):
            for part in parts:
                if isinstance(part, str):
                    records.append({
                        &quot;title&quot;: title,
                        &quot;date&quot;: date_str,
                        &quot;role&quot;: role,
                        &quot;content&quot;: part
                    })

# Create a DataFrame
df = pd.DataFrame(records)

# Extract topics or keywords that seem significant
df[&quot;tags&quot;] = df[&quot;content&quot;].apply(lambda x: &quot;, &quot;.join(sorted(set(re.findall(r&quot;\b(chatgpt[- ]?[34]|openai|transcript[s]?)\b&quot;, x, re.I)))))

