from textwrap import dedent

# Bash script content for a Termux-compatible chatbot wrapper
script_content = dedent(&quot;&quot;&quot;
#!/data/data/com.termux/files/usr/bin/bash

MODEL_PATH=&quot;../../models/mistral-base.gguf&quot;
LLAMA_BIN=&quot;./llama-run&quot;
DEFAULT_OPTS=&quot;--threads 4 --color -n 150 --temp 0.7 --top-k 50&quot;

while true; do
    echo
    read -p &quot;You: &quot; prompt
    if [ &quot;$prompt&quot; == &quot;exit&quot; ]; then
        echo &quot;Goodbye.&quot;
        break
    fi
    $LLAMA_BIN file://$(pwd)/$MODEL_PATH &quot;$prompt&quot; $DEFAULT_OPTS
done
&quot;&quot;&quot;).strip()

# Save the script to a file
script_path = &quot;/mnt/data/ai-chat.sh&quot;
with open(script_path, &quot;w&quot;) as f:
    f.write(script_content)

script_path

