# Updated bash script with TTS integration using espeak

script_with_tts = dedent(&quot;&quot;&quot;
#!/data/data/com.termux/files/usr/bin/bash

MODEL_PATH=&quot;../../models/mistral-base.gguf&quot;
LLAMA_BIN=&quot;./llama-run&quot;
DEFAULT_OPTS=&quot;--threads 4 --color -n 150 --temp 0.7 --top-k 50&quot;

while true; do
    echo
    read -p &quot;You: &quot; prompt
    if [ &quot;$prompt&quot; == &quot;exit&quot; ]; then
        echo &quot;Goodbye.&quot;
        break
    fi

    response=$( $LLAMA_BIN file://$(pwd)/$MODEL_PATH &quot;$prompt&quot; $DEFAULT_OPTS )
    echo &quot;$response&quot;
    echo &quot;$response&quot; | espeak
done
&quot;&quot;&quot;).strip()

# Save the updated script with TTS
script_with_tts_path = &quot;/mnt/data/ai-chat-tts.sh&quot;
with open(script_with_tts_path, &quot;w&quot;) as f:
    f.write(script_with_tts)

script_with_tts_path

