# Full assistant script with espeak TTS and optional Whisper STT integration (as a placeholder structure)

full_script = dedent(&quot;&quot;&quot;
#!/data/data/com.termux/files/usr/bin/bash

MODEL_PATH=&quot;../../models/mistral-base.gguf&quot;
LLAMA_BIN=&quot;./llama-run&quot;
DEFAULT_OPTS=&quot;--threads 4 --color -n 150 --temp 0.7 --top-k 50&quot;

echo &quot;Voice Assistant Mode (text in, spoken out)&quot;
echo &quot;Say &#x27;exit&#x27; to quit.&quot;

while true; do
    echo
    read -p &quot;You: &quot; prompt
    if [ &quot;$prompt&quot; == &quot;exit&quot; ]; then
        echo &quot;Goodbye.&quot;
        break
    fi

    response=$( $LLAMA_BIN file://$(pwd)/$MODEL_PATH &quot;$prompt&quot; $DEFAULT_OPTS )
    echo &quot;$response&quot;
    echo &quot;$response&quot; | espeak -v en-us -s 140
done
&quot;&quot;&quot;).strip()

# Save the complete assistant script
full_script_path = &quot;/mnt/data/ai-assistant.sh&quot;
with open(full_script_path, &quot;w&quot;) as f:
    f.write(full_script)

full_script_path

