from textwrap import dedent

# Script to enforce strict turn-based interaction with the model
turn_based_script = dedent(&quot;&quot;&quot;
#!/data/data/com.termux/files/usr/bin/bash

MODEL_PATH=&quot;../../models/mistral-base.gguf&quot;
LLAMA_BIN=&quot;./llama-run&quot;
DEFAULT_OPTS=&quot;--threads 4 --color -n 150 --temp 0.7 --top-k 50&quot;

while true; do
    echo
    read -p &quot;You: &quot; prompt
    if [ &quot;$prompt&quot; == &quot;exit&quot; ]; then
        echo &quot;Goodbye.&quot;
        break
    fi

    echo &quot;Generating response...&quot;
    echo &quot;&quot;
    $LLAMA_BIN file://$(pwd)/$MODEL_PATH &quot;$prompt&quot; $DEFAULT_OPTS | head -n 50
    echo &quot;&quot;
    echo &quot;--- End of Response ---&quot;
done
&quot;&quot;&quot;).strip()

# Save the script to a new file
turn_script_path = &quot;/mnt/data/ai-turn-based.sh&quot;
with open(turn_script_path, &quot;w&quot;) as f:
    f.write(turn_based_script)

turn_script_path

