from textwrap import dedent

# Aggressive cleaner script for Void Linux inside Termux
aggressive_script = dedent(&quot;&quot;&quot;
#!/bin/bash

echo &quot;=== ANDROID CLEANUP: AGGRESSIVE MODE ===&quot;

TRASH_DIR=&quot;/storage/cleaned_junk&quot;
mkdir -p &quot;$TRASH_DIR&quot;

echo &quot;[1] Finding large files &gt;100MB...&quot;
find /storage -type f -size +100M -exec mv -v {} &quot;$TRASH_DIR/&quot; \\;

echo &quot;[2] Finding duplicate files (by hash)...&quot;
declare -A file_hashes

while IFS= read -r -d &#x27;&#x27; file; do
    hash=$(md5sum &quot;$file&quot; | awk &#x27;{print $1}&#x27;)
    if [[ -n &quot;${file_hashes[$hash]}&quot; ]]; then
        echo &quot;Duplicate: $file&quot;
        mv -v &quot;$file&quot; &quot;$TRASH_DIR/&quot;
    else
        file_hashes[$hash]=&quot;$file&quot;
    fi
done &lt; &lt;(find /storage -type f -name &quot;*.jpg&quot; -o -name &quot;*.png&quot; -print0)

echo &quot;[3] Scanning for blurry images...&quot;
for img in $(find /storage -type f \\( -iname &quot;*.jpg&quot; -o -iname &quot;*.png&quot; \\)); do
    clarity=$(convert &quot;$img&quot; -colorspace Gray -edge 1 -format &quot;%[standard-deviation]&quot; info:)
    blur=$(echo &quot;$clarity &lt; 0.005&quot; | bc)
    if [ &quot;$blur&quot; -eq 1 ]; then
        echo &quot;Blurry: $img&quot;
        mv -v &quot;$img&quot; &quot;$TRASH_DIR/&quot;
    fi
done

echo &quot;✓ Aggressive cleaning complete.&quot;
echo &quot;Moved all junk to: $TRASH_DIR&quot;
&quot;&quot;&quot;).strip()

# Save the script to a file
script_path = &quot;/mnt/data/clean.sh&quot;
with open(script_path, &quot;w&quot;) as f:
    f.write(aggressive_script)

script_path

